mapper.select:
select * from tbl_product p left join tbl_product_img i on p.pno = i.pno
where p.del='N' and p.pno > 0 and uuid != "" and p.sno=#{sno}
group by i.pno
order by p.pno desc limit #{dto.cri.skip} ,#{dto.cri.amount}

select * from tbl_product p left join tbl_product_img i on p.pno = i.pno left join tbl_store s on p.sno = s.sno right join tbl_category c on c.pno = p.pno where s.sno > 0 and p.del='N' and p.pno > 0 and c.cateno > 0 and uuid != ""
and s.sno=#{sno}
and p.state='O'
and c.catename=#{dto.cri.category}
and p.pname like concat("%", #{dto.cri.keyword} ,"%")
group by p.pno
order by p.price desc
order by p.price asc
order by p.pno desc
limit #{dto.cri.skip} ,#{dto.cri.amount}

mapper.update:
update tbl_product
set del='Y'
where pno=${pno}

update tbl_product_option
set qty = #{qty}
where
opno=#{opno}

mapper.select:
select count(*) from tbl_product p left join tbl_store s on p.sno = s.sno right join tbl_category c on c.pno = p.pno where p.pno > 0
and s.sno=#{sno}
and p.state='O'
and c.catename=#{cri.category}
and pname like concat("%", #{cri.keyword} ,"%")

mapper.update:
update tbl_product set questionreplycnt =
questionreplycnt + #{amount}
where pno = #{pno}

update tbl_product set reviewreplycnt =
reviewreplycnt + #{amount}
where pno = #{pno}
