mapper.select:
select * from tbl_seller s left join tbl_seller_auth a on s.sid = a.sid where s.sid != "" and s.sid !="admin" and auth is not null
and s.sname like concat("%", #{dto.cri.keyword} ,"%")
and auth = #{dto.cri.category}
order by regdate desc limit #{dto.cri.skip}, #{dto.cri.amount}

mapper.update:
update tbl_seller_auth 
set auth = #{auth}
where sid =#{sid}
